import React, { useEffect, useState } from 'react';
import { FiDownload, FiSmartphone, FiX } from 'react-icons/fi';

const InstallPWA = () => {
  const [supportsPWA, setSupportsPWA] = useState(false);
  const [promptInstall, setPromptInstall] = useState(null);
  const [isInstalled, setIsInstalled] = useState(false);
  const [showGuide, setShowGuide] = useState(false);

  useEffect(() => {
    // Check if already installed
    if (window.matchMedia('(display-mode: standalone)').matches) {
      setIsInstalled(true);
      return;
    }

    // Check for iOS
    const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent) && !window.MSStream;
    if (isIOS) {
      setSupportsPWA(true);
      setShowGuide(true);
      return;
    }

    // Handle install prompt for Android/Desktop
    const handler = (e) => {
      e.preventDefault();
      setSupportsPWA(true);
      setPromptInstall(e);
    };

    window.addEventListener('beforeinstallprompt', handler);

    return () => window.removeEventListener('beforeinstallprompt', handler);
  }, []);

  const handleInstallClick = async () => {
    if (!promptInstall) {
      setShowGuide(true);
      return;
    }

    promptInstall.prompt();
    const { outcome } = await promptInstall.userChoice;

    if (outcome === 'accepted') {
      setIsInstalled(true);
      setSupportsPWA(false);
    }

    setPromptInstall(null);
  };

  // Don't show if already installed or doesn't support PWA
  if (isInstalled || (!supportsPWA && !showGuide)) {
    return null;
  }

  return (
    <>
      {/* Install Button - Mobile Bottom */}
      {supportsPWA && !showGuide && (
        <div
          style={{
            position: 'fixed',
            bottom: '20px',
            left: '50%',
            transform: 'translateX(-50%)',
            zIndex: 9999,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: '10px',
            animation: 'slideUp 0.5s ease-out'
          }}
        >
          <button
            onClick={handleInstallClick}
            style={{
              background: 'linear-gradient(135deg, #FF7043 0%, #FF5722 100%)',
              color: 'white',
              border: 'none',
              borderRadius: '50px',
              padding: '15px 30px',
              fontSize: '16px',
              fontWeight: 'bold',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              gap: '10px',
              boxShadow: '0 4px 20px rgba(255, 112, 67, 0.4)',
              transition: 'transform 0.3s ease'
            }}
            onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
            onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
          >
            <FiSmartphone size={20} />
            Telefona Yükle
            <FiDownload size={20} />
          </button>

          <button
            onClick={() => setSupportsPWA(false)}
            style={{
              background: 'rgba(255, 255, 255, 0.9)',
              color: '#666',
              border: '1px solid #ddd',
              borderRadius: '20px',
              padding: '5px 15px',
              fontSize: '12px',
              cursor: 'pointer'
            }}
          >
            Daha sonra hatırlat
          </button>
        </div>
      )}

      {/* Installation Guide Modal */}
      {showGuide && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0, 0, 0, 0.8)',
            zIndex: 10000,
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            padding: '20px'
          }}
        >
          <div
            style={{
              background: 'white',
              borderRadius: '20px',
              padding: '30px',
              maxWidth: '400px',
              width: '100%',
              maxHeight: '80vh',
              overflow: 'auto'
            }}
          >
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '20px'
            }}>
              <h2 style={{ margin: 0, color: '#1E3A5F' }}>
                📱 Uygulamayı Yükleme
              </h2>
              <button
                onClick={() => setShowGuide(false)}
                style={{
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  padding: '5px'
                }}
              >
                <FiX size={24} color="#666" />
              </button>
            </div>

            {/* iOS Instructions */}
            {/iPad|iPhone|iPod/.test(navigator.userAgent) && (
              <div style={{ color: '#333' }}>
                <h3 style={{ color: '#FF7043', fontSize: '18px' }}>
                  iPhone/iPad için:
                </h3>
                <ol style={{ paddingLeft: '20px', lineHeight: '1.8' }}>
                  <li>Safari tarayıcısını kullanın</li>
                  <li>Alt kısımdaki <strong>Paylaş</strong> butonuna dokunun ⬆️</li>
                  <li><strong>"Ana Ekrana Ekle"</strong> seçeneğini bulun ➕</li>
                  <li>Sağ üstten <strong>"Ekle"</strong> butonuna dokunun</li>
                  <li>Uygulama ana ekranınıza eklenecek! 🎉</li>
                </ol>
              </div>
            )}

            {/* Android Instructions */}
            {/Android/.test(navigator.userAgent) && (
              <div style={{ color: '#333' }}>
                <h3 style={{ color: '#FF7043', fontSize: '18px' }}>
                  Android için:
                </h3>
                <ol style={{ paddingLeft: '20px', lineHeight: '1.8' }}>
                  <li>Chrome tarayıcısını kullanın</li>
                  <li>Sağ üstteki 3 nokta menüsüne dokunun ⋮</li>
                  <li><strong>"Ana ekrana ekle"</strong> seçeneğini bulun</li>
                  <li><strong>"Ekle"</strong> butonuna dokunun</li>
                  <li>Uygulama ana ekranınıza eklenecek! 🎉</li>
                </ol>
              </div>
            )}

            {/* Desktop Instructions */}
            {!/Android|iPad|iPhone|iPod/.test(navigator.userAgent) && (
              <div style={{ color: '#333' }}>
                <h3 style={{ color: '#FF7043', fontSize: '18px' }}>
                  Bilgisayar için:
                </h3>
                <ol style={{ paddingLeft: '20px', lineHeight: '1.8' }}>
                  <li>Chrome tarayıcısını kullanın</li>
                  <li>Adres çubuğunun sağındaki <strong>Yükle</strong> simgesine tıklayın ⬇️</li>
                  <li><strong>"Yükle"</strong> butonuna tıklayın</li>
                  <li>Uygulama bilgisayarınıza yüklenecek! 🎉</li>
                </ol>
              </div>
            )}

            <div style={{
              background: '#f0f8ff',
              padding: '15px',
              borderRadius: '10px',
              marginTop: '20px'
            }}>
              <p style={{ margin: 0, fontSize: '14px', color: '#666' }}>
                💡 <strong>İpucu:</strong> Yüklendikten sonra normal bir uygulama gibi kullanabilirsiniz!
              </p>
            </div>
          </div>
        </div>
      )}

      <style jsx>{`
        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateX(-50%) translateY(100px);
          }
          to {
            opacity: 1;
            transform: translateX(-50%) translateY(0);
          }
        }
      `}</style>
    </>
  );
};

export default InstallPWA;
