import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import { toast } from 'react-toastify';
import { ClipLoader } from 'react-spinners';
import { FiDownload, FiFileText, FiUser, FiBriefcase, FiPlus } from 'react-icons/fi';
import '../styles/application-form.css';

const ApplicationForm = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState(null);
  const [pdfPath, setPdfPath] = useState(null);

  const { register, handleSubmit, setValue, formState: { errors } } = useForm();

  useEffect(() => {
    if (user) {
      setValue('ad', user.ad);
      setValue('soyad', user.soyad);
      setValue('tc_kimlik', user.tc_kimlik);
      setValue('telefon', user.telefon);
      setValue('email', user.email);
      setValue('dogum_tarihi', user.dogum_tarihi);
      setValue('dogum_yeri', user.dogum_yeri);
      setValue('sicil_no', user.sicil_no);
      setValue('unvan', user.unvan);
      setValue('mezun_universite', user.mezun_universite);
      setValue('uyum_egitimi_sertifika', user.uyum_egitimi_sertifika);
    }
  }, [user, setValue]);

  const onSubmit = async (data) => {
    if (!user.telefon_dogrulanmis) {
      toast.error('Ba≈üvuru yapmak i√ßin telefon numaranƒ±zƒ± doƒürulamalƒ±sƒ±nƒ±z');
      return;
    }

    try {
      setLoading(true);

      const response = await axios.post('/api/pdf/basvuru-formu', data);

      if (response.data.success) {
        toast.success('Ba≈üvuru formu olu≈üturuldu');
        setFormData(data);
        setPdfPath(response.data.pdfPath);
      }
    } catch (error) {
      toast.error('Form olu≈üturulamadƒ±');
    } finally {
      setLoading(false);
    }
  };

  const downloadPDF = () => {
    if (pdfPath) {
      const fileName = pdfPath.split('/').pop();
      const link = document.createElement('a');
      link.href = `/exports/${fileName}`;
      link.download = fileName;
      link.click();
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <div className="modern-card" style={{
        textAlign: 'center',
        background: 'linear-gradient(135deg, #f44336 0%, #e91e63 100%)',
        color: 'white',
        marginBottom: '30px'
      }}>
        <span className="emoji-lg">üìù</span>
        <h1 style={{ fontSize: '32px', marginTop: '20px' }}>Ba≈üvuru Formu</h1>
        <p style={{ fontSize: '18px', opacity: 0.9 }}>üè• Aile Hekimliƒüi Kura Ba≈üvurusu</p>
      </div>

      {!user.telefon_dogrulanmis && (
        <div className="modern-card" style={{
          background: 'linear-gradient(135deg, #ff9800 0%, #f57c00 100%)',
          color: 'white',
          marginBottom: '30px'
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            <span className="emoji-md">‚ö†Ô∏è</span>
            <div>
              <strong>Telefon Doƒürulamasƒ± Gerekli!</strong>
              <p style={{ margin: '5px 0 0 0', opacity: 0.9 }}>
                Ba≈üvuru formu olu≈üturabilmek i√ßin √∂ncelikle telefon numaranƒ±zƒ± doƒürulamanƒ±z gerekmektedir.
              </p>
            </div>
          </div>
        </div>
      )}

      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="modern-card">
          <h3 style={{ display: 'flex', alignItems: 'center', marginBottom: '25px', color: '#333' }}>
            <span className="emoji-md" style={{ marginRight: '10px' }}>üë§</span>
            Ki≈üisel Bilgiler
          </h3>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px' }}>
            <div className="form-group">
              <label className="modern-label">üÜî TC Kimlik No</label>
              <input
                type="text"
                className="modern-input"
                {...register('tc_kimlik')}
                disabled
              />
            </div>

            <div className="form-group">
              <label className="modern-label">‚úèÔ∏è Ad</label>
              <input
                type="text"
                className="modern-input"
                {...register('ad', { required: 'Ad zorunludur' })}
              />
              {errors.ad && <span className="error-message">{errors.ad.message}</span>}
            </div>

            <div className="form-group">
              <label className="modern-label">‚úèÔ∏è Soyad</label>
              <input
                type="text"
                className="modern-input"
                {...register('soyad', { required: 'Soyad zorunludur' })}
              />
              {errors.soyad && <span className="error-message">{errors.soyad.message}</span>}
            </div>

            <div className="form-group">
              <label className="modern-label">üìÖ Doƒüum Tarihi</label>
              <input
                type="date"
                className="modern-input"
                {...register('dogum_tarihi')}
              />
            </div>

            <div className="form-group">
              <label className="modern-label">üìç Doƒüum Yeri</label>
              <input
                type="text"
                className="modern-input"
                {...register('dogum_yeri')}
              />
            </div>

            <div className="form-group">
              <label className="modern-label">üì± Telefon</label>
              <input
                type="text"
                className="modern-input"
                {...register('telefon', { required: 'Telefon zorunludur' })}
              />
              {errors.telefon && <span className="error-message">{errors.telefon.message}</span>}
            </div>

            <div className="form-group">
              <label className="modern-label">üìß E-posta</label>
              <input
                type="email"
                className="modern-input"
                {...register('email', { required: 'E-posta zorunludur' })}
              />
              {errors.email && <span className="error-message">{errors.email.message}</span>}
            </div>
          </div>
        </div>

        <div className="modern-card">
          <h3 style={{ display: 'flex', alignItems: 'center', marginBottom: '25px', color: '#333' }}>
            <span className="emoji-md" style={{ marginRight: '10px' }}>üë®‚Äç‚öïÔ∏è</span>
            Mesleki Bilgiler
          </h3>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '20px' }}>
            <div className="form-group">
              <label className="modern-label">üîñ Sicil No</label>
              <input
                type="text"
                className="modern-input"
                {...register('sicil_no')}
                placeholder="Doktor sicil numaranƒ±z"
              />
            </div>

            <div className="form-group">
              <label className="modern-label">üéì √únvan</label>
              <select
                className="modern-input"
                {...register('unvan', { required: '√únvan se√ßimi zorunludur' })}
              >
                <option value="">Se√ßiniz</option>
                <option value="Aile Hekimliƒüi Uzmanƒ±">Aile Hekimliƒüi Uzmanƒ±</option>
                <option value="Uzman Tabip">Uzman Tabip</option>
                <option value="Pratisyen">Pratisyen</option>
              </select>
              {errors.unvan && <span className="error-message">{errors.unvan.message}</span>}
            </div>

            <div className="form-group">
              <label className="modern-label">üè´ Mezun Olduƒüu √úniversite</label>
              <input
                type="text"
                className="modern-input"
                {...register('mezun_universite')}
              />
            </div>

            <div className="form-group">
              <label className="modern-label">üìú Uyum Eƒüitimi Sertifika No</label>
              <input
                type="text"
                className="modern-input"
                {...register('uyum_egitimi_sertifika')}
                placeholder="Varsa giriniz"
              />
            </div>
          </div>
        </div>

        <div className="modern-card">
          <h3 style={{ display: 'flex', alignItems: 'center', marginBottom: '25px', color: '#333' }}>
            <span className="emoji-md" style={{ marginRight: '10px' }}>‚ûï</span>
            Ek Bilgiler
          </h3>

          <div className="form-group">
            <label className="modern-label">üó∫Ô∏è Tercih Edilen ƒ∞l√ßeler</label>
            <input
              type="text"
              className="modern-input"
              {...register('tercih_ilceler')}
              placeholder="√ñrn: Kadƒ±k√∂y, √úsk√ºdar, Be≈üikta≈ü (virg√ºlle ayƒ±rƒ±n)"
            />
          </div>

          <div className="form-group">
            <label className="modern-label">üí≠ A√ßƒ±klama / Notlar</label>
            <textarea
              className="modern-input"
              {...register('aciklama')}
              rows="4"
              placeholder="Varsa eklemek istediƒüiniz notlar"
              style={{ borderRadius: '20px', resize: 'vertical' }}
            />
          </div>
        </div>

        <div className="modern-card" style={{ display: 'flex', gap: '15px', justifyContent: 'center', flexWrap: 'wrap' }}>
          <button
            type="submit"
            className="btn-modern btn-gradient-blue"
            disabled={loading || !user.telefon_dogrulanmis}
            style={{ minWidth: '200px' }}
          >
            {loading ? (
              <>
                <div className="spinner-modern" style={{ width: '20px', height: '20px', display: 'inline-block', marginRight: '10px' }}></div>
                ƒ∞≈üleniyor...
              </>
            ) : (
              <>üöÄ Ba≈üvuru Formu Olu≈ütur</>
            )}
          </button>

          {pdfPath && (
            <button
              type="button"
              className="btn-modern btn-gradient-green"
              onClick={downloadPDF}
              style={{ minWidth: '200px' }}
            >
              <FiDownload style={{ marginRight: '5px' }} /> üìÑ PDF ƒ∞ndir
            </button>
          )}
        </div>
      </form>

      {formData && pdfPath && (
        <div className="modern-card" style={{
          background: 'linear-gradient(135deg, #4caf50 0%, #45a049 100%)',
          color: 'white',
          textAlign: 'center',
          marginTop: '30px'
        }}>
          <span className="emoji-lg">‚úÖ</span>
          <h3 style={{ marginTop: '20px', marginBottom: '15px' }}>
            Ba≈üvuru Formu Ba≈üarƒ±yla Olu≈üturuldu!
          </h3>
          <p style={{ fontSize: '16px', opacity: 0.95, marginBottom: '10px' }}>
            üìã Formunuz ba≈üarƒ±yla olu≈üturuldu ve kaydedildi.
          </p>
          <p style={{ fontSize: '16px', opacity: 0.9 }}>
            üñ®Ô∏è PDF dosyanƒ±zƒ± indirip √ßƒ±ktƒ± alabilir ve ilgili kuruma teslim edebilirsiniz.
          </p>
        </div>
      )}
    </div>
  );
};

export default ApplicationForm;
