import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { useForm } from 'react-hook-form';
import { ClipLoader } from 'react-spinners';

const Login = () => {
  const { login } = useAuth();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const { register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = async (data) => {
    setLoading(true);
    const result = await login(data.username, data.password);
    setLoading(false);

    if (result.success) {
      navigate('/');
    }
  };

  return (
    <div className="card" style={{ maxWidth: '500px', margin: '50px auto' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '30px' }}>Giriş Yap</h2>

      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-group">
          <label className="form-label">
            TC Kimlik / Telefon / E-posta
          </label>
          <input
            type="text"
            className="form-control"
            {...register('username', { required: 'Bu alan zorunludur' })}
            placeholder="TC Kimlik No, telefon veya e-posta"
          />
          {errors.username && (
            <span className="error-message">{errors.username.message}</span>
          )}
        </div>

        <div className="form-group">
          <label className="form-label">Şifre</label>
          <input
            type="password"
            className="form-control"
            {...register('password', { required: 'Şifre zorunludur' })}
            placeholder="Şifrenizi girin"
          />
          {errors.password && (
            <span className="error-message">{errors.password.message}</span>
          )}
        </div>

        <button
          type="submit"
          className="btn btn-primary"
          style={{ width: '100%', marginTop: '20px' }}
          disabled={loading}
        >
          {loading ? <ClipLoader color="#fff" size={20} /> : 'Giriş Yap'}
        </button>
      </form>

      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <p>
          Hesabınız yok mu?{' '}
          <Link to="/register" style={{ color: '#007bff' }}>
            Kayıt Ol
          </Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
